function provera()
{
    var uzorak_name = document.forma.name.value;
    var uzorak_phone = document.forma.phone.value;
    var uzorak_email = document.forma.email.value;
    var uzorak_checkin_date = document.forma.checkin_date.value;
    var uzorak_checkout_date = document.forma.checkout_date.value;
    if(uzorak_name==="" || uzorak_name ===null)
    {
        alert("Niste uneli ime");
        return false;
    }
    else if(uzorak_phone==="" || uzorak_phone ===null)
    {
        alert("Niste uneli telefon");
        return false;
    }
    else if(uzorak_email==="" || uzorak_email ===null)
    {
        alert("Niste uneli email");
        return false;
    }
    else if(uzorak_checkin_date==="" || uzorak_checkin_date ===null)
    {
        alert("Niste uneli datum dolaska");
        return false;
    }
    else if(uzorak_checkout_date==="" || uzorak_checkout_date ===null)
    {
        alert("Niste uneli datum odlaska");
        return false;
    }
    else {
        alert("Uspesno ste poslali formu");
        return true;

    }

}

const processRezervaciju = () => {
    const dolazak = document.rezervacija.checkin_date.value;
    const odlazak = document.rezervacija.checkout_date.value;
    const osoba = document.rezervacija.adults.value;
    const deca = document.rezervacija.children.value;

    const url = './pages/rezervacija.html?dolazak=' +
     encodeURIComponent(dolazak) + 
     '&odlazak=' + encodeURIComponent(odlazak) +
     '&osoba=' + encodeURIComponent(osoba) +
     '&deca=' + encodeURIComponent(deca);
     
    // Redirect to another page
    window.location.href = url;
}

const saveFile = () => {
  
    const name = document.forma.name.value;
    const phone = document.forma.phone.value;
    const email = document.forma.email.value;
    const checkin_date = document.forma.checkin_date.value;
    const checkout_date = document.forma.checkout_date.value;
    const adults = document.forma.adults.value;
    const children = document.forma.children.value;
    const room = document.forma.room.value;
    const message = document.forma.message.value;

    
    let data =
        'Potvrda o Rezervaciji' + ' \r\n ' + ' \r ' +
        'Ime: ' + name + ' \r\n ' +
        'Telefon: ' + phone + ' \r\n ' +
        'Email: ' + email + ' \r\n ' +
        'Datum Dolaska: ' + checkin_date + ' \r\n ' +
        'Datum Odlaska: ' + checkout_date + ' \r\n ' +
        'Odrasli: ' + adults + ' \r\n ' +
        'Deca: ' + children + ' \r\n ' +
        'Odabrana soba: ' + room + ' \r\n ' +
        'Komentar: ' + message;

    
    const textblob = new Blob([data], { type: 'text/plain' });
    const imefajla = 'Rezervacija.txt'; 

    let novilink = document.createElement("a");
    novilink.download = imefajla;

    if (window.webkitURL != null) {
        novilink.href = window.webkitURL.createObjectURL(textblob);
    } else {
        novilink.href = window.URL.createObjectURL(textblob);
        novilink.style.display = "none";
        document.body.appendChild(novilink);
    }

    novilink.click();
}

